const catalogModal=document.querySelector(".catalog-modal"),cardName=document.querySelectorAll(".card-name"),closeModalButton=catalogModal.querySelector(".catalog-modal-close");for(let a=0;a<cardName.length;a++)cardName[a].addEventListener("click",function(a){a.preventDefault,catalogModal.classList.add("catalog-modal-show")});closeModalButton.addEventListener("click",function(a){a.preventDefault,catalogModal.classList.remove("catalog-modal-show")}),window.addEventListener("keydown",function(a){27===a.keyCode&&(a.preventDefault,catalogModal.classList.remove("catalog-modal-show"))});